def get_real_time(v_str):
    """弹幕出现时间: 把秒转换为时分秒"""
    total_seconds = int(v_str.split('.')[0])
    second = total_seconds % 60
    hour = total_seconds // 60 // 60
    minute = total_seconds // 60 % 60
    ret_time = str(hour)+"时"+str(minute)+"分"+str(second)+"秒"
    print(ret_time)
    return ret_time


